        if (this.form) {
            this.form.unregister();
            this.form = undefined;
        }
        if (this.backToTop) {
            this.backToTop.unregister();
            this.backToTop = undefined;
        }
        this.unregisterCollapses();
        this.removeEventListeners();
        NCIBigFooter._components.delete(this.element);
